mapper.select:
	select * 
	from (
		select rownum rn,A.* 
		from(
			select num, part, title, writer, pw, content, checking, step, status, 
           		case when to_char(rdate,'yyyy-MM-dd') = to_char(sysdate,'yyyy-MM-dd') then 'New'    
				else '' end as sub 
           	from inquiry
           	order by num desc)A)
	   where rn between #{param1} and #{param2}

select count(*)
from inquiry
where num = #{param1} and pw = #{param2}

mapper.update:
update inquiry
set readcnt = readcnt+1
where num= #{param1}

update inquiry
set part = #{param2}, title = #{param3}, writer = #{param4}, pw = #{param5}, content = #{param6}, checking = #{param7}, status = 'n'
where num= #{param1}

update inquiry
set status = 'y'
where num= #{param1}

mapper.select:
select *
from inquiry comment
where num = #{param1}

mapper.update:
update inquiry
set step = #{param2}
where num= #{param1}

mapper.select:
select NVL(max(step),0)
from inquirycomment
where num = #{param1}
