mapper.select:
select playernum, pimage, tname, pname, pnumber, to_char(pbirth,'yyyy-MM-dd')pbirth, height, weight, main, pchar, clickup, step, play
 from player_soccer 
where playernum=#{param1}

select count(*)
from splayer_comment
where playernum = #{param1}

select * 
from (
	select rownum rn,A.* 
	from(  
		select playernum, step, id, writer, ucomment, 
			case  to_char(cdate,'yyyy-MM-dd') when to_char(sysdate,'yyyy-MM-dd')then to_char(cdate,'HH24:MI')
			else to_char(cdate,'yy/MM/dd') end  as cdate
			,heart
		from splayer_comment
		where playernum = #{param1}
		order by step desc)A)
   where rn between #{param2} AND #{param3}

select NVL(max(step),0)
from splayer_comment 
where playernum=#{param1}

mapper.update:
update player_soccer 
set tname=#{param1}, pname=#{param2}, pnumber=#{param3}, pbirth=#{param4}, height=#{param5}, weight=#{param6}, main=#{param7}, pimage=#{param8}, pchar=#{param9} 
where playernum=#{param10}
